import { Switch, Route, Link } from "react-router-dom";
import { Layout, Typography, Space } from "antd";

import Navbar from "./components/Navbar";
import Homepage from "./components/Homepage";
import Exchanges from "./components/Exchanges";
import Cryptocurrencies from "./components/Cryptocurrencies";
import CryptoDetails from "./components/CryptoDetails";
import News from "./components/News";

import "./App.css";
import { useState } from "react";

function App() {
  let [coin, SetCoin] = useState(0);
  let [market, SetMarket] = useState(0);
  const getData = async () => {
    await axios
      .get("https://coinranking1.p.rapidapi.com/exchanges", {
        headers: {
          "x-rapidapi-host": "coinranking1.p.rapidapi.com",
          "x-rapidapi-key":
            "bf4e2abd1emsh95c84a25d1cac11p150d26jsnbc1a2b0c71c9",
        },
      })
      .then((a) => {
        SetCoin(a.data.data);
      });

    await axios
      .get("https://coinranking1.p.rapidapi.com/coins", {
        headers: {
          "x-rapidapi-host": "coinranking1.p.rapidapi.com",
          "x-rapidapi-key":
            "bf4e2abd1emsh95c84a25d1cac11p150d26jsnbc1a2b0c71c9",
        },
      })
      .then((a) => {
        SetMarket(a.data.data.stats);
      });
  };

  return (
    <div className="app">
      <div className="navbar">
        <Navbar />
      </div>
      <div className="main">
        <Layout>
          <div className="routes">
            <Switch>
              <Route exact path="/">
                <Homepage />
              </Route>
              <Route exact path="/exchanges">
                <Exchanges />
              </Route>
              <Route exact path="/cryptocurrencies">
                <Cryptocurrencies />
              </Route>
              <Route exact path="/crypto/:coinId">
                <CryptoDetails />
              </Route>
              <Route exact path="/news">
                <News />
              </Route>
            </Switch>
          </div>
        </Layout>

        <div className="footer">
          <Typography.Title style={{ color: "white", textAlign: "cneter" }}>
            Cryptoverse <br />
            All rights reserverd
          </Typography.Title>
          <Space>
            <Link to="/">Home</Link>
            <Link to="/exchanges">Exchanges</Link>
            <Link to="/news">News</Link>
          </Space>
        </div>
      </div>
    </div>
  );
}

export default App;
