    'x-rapidapi-host': 'coinranking1.p.rapidapi.com',
    'x-rapidapi-key': 'bf4e2abd1emsh95c84a25d1cac11p150d26jsnbc1a2b0c71c9'
  }
};

axios.request(options).then(function (response) {
	console.log(response.data);
}).catch(function (error) {
	console.error(error);
});
    }
    console.log(data.data.stats)


    return(
    <>
     <Title className="heading">
     Global Crypto Stats
    </Title>
    <Button onClick={getData}>getData</Button>

        <Row>
        <Col span={12}><Statistic title="Total Cryptocurrencies" value={Math.floor(data.data.stats.volume)}/></Col>
        <Col span={12}><Statistic title="Total Exchanges" value={data.data.exchanges.length} /></Col>
        <Col span={12}><Statistic title="Total Market Cap" value="5" /></Col>
        <Col span={12}><Statistic title="Total 24h Volume" value="5" /></Col>
        <Col span={12}><Statistic title="Total Markets" value="5" /></Col>
        </Row>
    </>
    )
}

export default Homepage
