import millify from 'millify'
import { Link } from 'react-router-dom'
import { Card, Row, Col } from 'antd'
import { useState } from 'react'
import axios from 'axios'

const Cryptocurrencies = () =>{

    const [cryptos, SetCryptos] = useState(0)

    if(cryptos === 0){
        axios.get("https://coinranking1.p.rapidapi.com/coins", {
            headers: {
              "x-rapidapi-host": "coinranking1.p.rapidapi.com",
              "x-rapidapi-key": "bf4e2abd1emsh95c84a25d1cac11p150d26jsnbc1a2b0c71c9",
            },
          })
          .then((a) => {
            SetCryptos(a.data.data.coins);
          });
    }




    return(
        <>
        hojin
        <Row gutters={[32,32]} className="crypto-card-container">

            {cryptos === 0 ?  <Col>Loading~~</Col> : cryptos.map((a)=>{
                {console.log(a)}
                <Col xs={24} xm={12} lg={6} className="crypto-card" key={a.id}>
                    hojin
                    <Link to={`/crypto/${a.id}`}>
                        <Card  extra={<img className="crypto-image" src={a.iconUrl} />} hoverable>
                            <p>Price : {millify(a.price)}</p>
                            <p>Market Cap : {millify(a.marketCap)}</p>
                            <p>Daily Change : {millify(a.change)}</p>
                        </Card>
                    </Link>
                </Col>
            })}
        </Row>
        </>
    )
}

export default Cryptocurrencies
