import millify from 'millify'
import { Link } from 'react-router-dom'
import { Card, Row, Col, Input, Typography, Button } from 'antd'
import { useState } from 'react'
import axios from 'axios'

const Cryptocurrencies = ({market}) =>{

    const [cryptos, SetCryptos] = useState(market)

    if(cryptos === 0){
        axios.get("https://coinranking1.p.rapidapi.com/coins", {
            headers: {
              "x-rapidapi-host": "coinranking1.p.rapidapi.com",
              "x-rapidapi-key": "bf4e2abd1emsh95c84a25d1cac11p150d26jsnbc1a2b0c71c9",
            },
          })
          .then((a) => {
            SetCryptos(a.data.data.coins);
          });
    }




    return(
        <>
        <Row gutters={[32,32]} className="crypto-card-container">
            {cryptos !== 0 ? cryptos.map((currency)=>{
                <Col xs={24} xm={12} lg={6} className="crypto-card" key={currency.id}>

                </Col>
            }) : <Col>Loading~~</Col>}

        </Row>
        </>
    )
}

export default Cryptocurrencies
