import { Typography, Row, Col, Statistic, Button } from "antd"
import millify from "millify"
import { Link } from "react-router-dom"
import axios from "axios"
import { useState } from "react"
import Cryptocurrencies from "./Cryptocurrencies"
import News from "./News"


const {Title} = Typography


const Homepage = () =>{

    let [coin, SetCoin] = useState(0);
    let [market, SetMarket] = useState(0);
const getData = async () =>{
    await  axios.get("https://coinranking1.p.rapidapi.com/exchanges", {
    headers: {
      "x-rapidapi-host": "coinranking1.p.rapidapi.com",
      "x-rapidapi-key": "bf4e2abd1emsh95c84a25d1cac11p150d26jsnbc1a2b0c71c9",
    },
  })
  .then((a) => {
    SetCoin(a.data.data);
  });

await axios.get("https://coinranking1.p.rapidapi.com/coins", {
    headers: {
      "x-rapidapi-host": "coinranking1.p.rapidapi.com",
      "x-rapidapi-key": "bf4e2abd1emsh95c84a25d1cac11p150d26jsnbc1a2b0c71c9",
    },
  })
  .then((a) => {
    SetMarket(a.data.data.stats);
  });
        }   


    return(
    <>
     <Title className="heading">
     Global Crypto Stats
    </Title>
    <Button onClick={getData}>getData</Button>
    {coin === 0 && market === 0? <Col>Data를 가져와 주세요</Col> : <Row>
        <Col span={12}><Statistic title="Total Cryptocurrencies" value={millify(coin.stats.total)}/></Col>
        <Col span={12}><Statistic title="Total Exchanges" value={coin.exchanges.length} /></Col>
        <Col span={12}><Statistic title="Total 24h Volume" value={millify(coin.stats.volume)} /></Col>
        <Col span={12}><Statistic title="Total Market Cap" value={Math.floor(market.totalMarketCap)} /></Col>
        <Col span={12}><Statistic title="Total Markets" value={Math.floor(market.totalMarkets)} /></Col>
        </Row>
        }
        <div className="home-heading-container">
            <Title className="home-title">Top 10 Cryptocurrencies in the world</Title>
            <Title className="show-more"><Link to="/cryptocurrencies"/>Show More</Title>
        </div>
        <Cryptocurrencies coin={coin}/>
        <div className="home-heading-container">
            <Title className="home-title">Latest Crypto News</Title>
            <Title className="show-more"><Link to="/News"/>Show More</Title>
        </div>
        <News simplified />
    </>
    )
}

export default Homepage
