import axios from "axios";

export let coin = 0;
export let Market = 0;

const getData = async () => {
  await axios
    .get("https://coinranking1.p.rapidapi.com/exchanges", {
      headers: {
        "x-rapidapi-host": "coinranking1.p.rapidapi.com",
        "x-rapidapi-key": "bf4e2abd1emsh95c84a25d1cac11p150d26jsnbc1a2b0c71c9",
      },
    })
    .then((a) => {
      coin = a.data.data;
      // SetCoin(a.data.data);
    });

  await await axios
    .get("https://coinranking1.p.rapidapi.com/coins", {
      headers: {
        "x-rapidapi-host": "coinranking1.p.rapidapi.com",
        "x-rapidapi-key": "bf4e2abd1emsh95c84a25d1cac11p150d26jsnbc1a2b0c71c9",
      },
    })
    .then((a) => {
      Market = a.data.stats;
      // SetMarket(a.data.data.stats);
    });
};
getData();

