import { Typography, Row, Col, Statistic, Button } from "antd"

import { Link } from "react-router-dom"
import axios from "axios"
import { useState } from "react"
const {Title} = Typography


const Homepage = () =>{
    let [coin,SetCoin] = useState(0)
    const getData = async () =>{
       await axios.get('https://coinranking1.p.rapidapi.com/exchanges',{
             headers: {
                'x-rapidapi-host': 'coinranking1.p.rapidapi.com',
            'x-rapidapi-key': 'bf4e2abd1emsh95c84a25d1cac11p150d26jsnbc1a2b0c71c9'
      }
        }).then((a)=>{
            SetCoin(a.data)
        })
        await axios.get("https://coinranking1.p.rapidapi.com/markets",{
    headers: {
        'x-rapidapi-host': 'coinranking1.p.rapidapi.com',
        'x-rapidapi-key': 'bf4e2abd1emsh95c84a25d1cac11p150d26jsnbc1a2b0c71c9'
      }
    }).then((a)=>{
        console.log(a.data)
    })

    };




    return(
    <>
     <Title className="heading">
     Global Crypto Stats
    </Title>
    <Button onClick={getData}>getData</Button>

        <Row>
        <Col span={12}><Statistic title="Total Cryptocurrencies" value={Math.floor(coin.data.stats.total)}/></Col>
        <Col span={12}><Statistic title="Total Exchanges" value={coin.data.exchanges.length} /></Col>
        <Col span={12}><Statistic title="Total Market Cap" value="5" /></Col>
        <Col span={12}><Statistic title="Total 24h Volume" value={Math.floor(coin.data.stats.volume)} /></Col>
        <Col span={12}><Statistic title="Total Markets" value="5" /></Col>
        </Row>
    </>
    )
}

export default Homepage
